import { Component, OnInit } from '@angular/core';
import { FormGroup } from "@angular/forms"
import { RxFormBuilder,RxFormGroup } from '@rxweb/reactive-form-validators';

import { User } from './user.model';

@Component({
    selector: 'app-patch-add',
    templateUrl: './patchmodelvalue-add.component.html'
})
export class PatchModelValueAddComponent implements OnInit {
    userInfoFormGroup: RxFormGroup

    constructor(
        private formBuilder: RxFormBuilder    ) { }

    ngOnInit() {
        let user = new User();
        this.userInfoFormGroup = <RxFormGroup>this.formBuilder.formGroup(user);
        this.userInfoFormGroup.patchModelValue({firstName:'Ajay'})
    }
}

