import { Component, OnInit } from '@angular/core';
import { FormGroup } from "@angular/forms"
import { RxFormBuilder,RxFormGroup,FormGroupExtension } from '@rxweb/reactive-form-validators';

import { Country } from './country.model';

@Component({
    selector: 'app-error-complete',
    templateUrl: './geterrorsummary-complete.component.html'
})
export class GetErrorSummaryCompleteComponent implements OnInit {
    
    countryFormGroup: RxFormGroup
  
    constructor(
        private formBuilder: RxFormBuilder    ) { }

    ngOnInit() {
        let country = new Country();
        this.countryFormGroup = <RxFormGroup>this.formBuilder.formGroup(country);
    }
    showError()
    {
      let errorObject = {};
      errorObject = this.countryFormGroup.getErrorSummary(true);
      console.log(errorObject);

    }
}

