complete.model:
import {  alphaAsync, } from   "@rxweb/reactive-form-validators" 
import { of } from "rxjs" 

   

export class AddressInfo {

	@alphaAsync() 
	countryName: string;
	
	
	//If you want to apply conditional expression of type 'function'
	@alphaAsync({conditionalExpression:(x,y) => x.countryName == "India" }) 
	countryCode: string;
	
	
	//If you want to apply conditional expression of type 'string'
	@alphaAsync({conditionalExpression:'x => x.countryName =="India"' }) 
	cityName: string;
	
	
	@alphaAsync({locale:ValidationAlphabetLocale.spanish }) 
	spanishcityName: string;
	
	
	@alphaAsync({allowWhiteSpace:true }) 
	stateName: string;
	
	
	@alphaAsync({message:'You can enter only alphabets.' }) 
	stateCode: string;
	
	
	@alphaAsync({messageKey:'alphaMessageKey' }) 
	cityCode: string;
	
	
}

