import { Component, OnInit } from '@angular/core';
import { FormGroup, FormBuilder } from "@angular/forms"
import { RxwebValidators } from '@rxweb/reactive-form-validators';

@Component({
    selector: 'app-bind-complete-validator',
    templateUrl: './bind-complete.component.html'
})
export class BindCompleteValidatorComponent implements OnInit {
    userInfoFormGroup: FormGroup

	constructor(
        private formBuilder: FormBuilder )
	{ }

    ngOnInit() {
        this.userInfoFormGroup = this.formBuilder.group({
            purchasePrice:[''], 
           resalePrice: ['', RxwebValidators.minNumber({
            dynamicConfig: (x, y) => {
                const minimumPrice = (x.purchasePrice * 1) + (x.purchasePrice * 30 / 100);
                  return { value: minimumPrice, message: `Resale price must be at least 30% more than Purchase price. Minimum resale price should be ${minimumPrice}` };
              }
             })] 
            
        });
    }
}

return.component:
import { Component, OnInit } from '@angular/core';
import { FormGroup, FormBuilder } from "@angular/forms"
import { RxwebValidators, NumericValueType } from '@rxweb/reactive-form-validators';

@Component({
    selector: 'app-bind-return-validator',
    templateUrl: './bind-return.component.html'
})
export class BindReturnValidatorComponent implements OnInit {
    userInfoFormGroup: FormGroup

    constructor(
        private formBuilder: FormBuilder) { }

    ngOnInit() {
        this.userInfoFormGroup = this.formBuilder.group({
            negativeNumber: ['', RxwebValidators.numeric({
                dynamicConfig: (x, y) => {
                    if (x.typeValue == 1)
                        return { acceptValue: NumericValueType.PositiveNumber }
                    else
                        return { acceptValue: NumericValueType.NegativeNumber }
                },
                allowDecimal: false,
                message: 'Positive Integer'
            })
            ]

        });
    }
}
