import { Component, OnInit } from '@angular/core';
import { FormGroup, FormBuilder } from "@angular/forms"
import { RxwebValidators } from '@rxweb/reactive-form-validators';

@Component({
    selector: 'app-containsAsync-complete-validator',
    templateUrl: './contains-async-complete.component.html'
})
export class ContainsAsyncCompleteValidatorComponent implements OnInit {
    userFormGroup: FormGroup

	constructor(
        private formBuilder: FormBuilder )
	{ }

    ngOnInit() {
        this.userFormGroup = this.formBuilder.group({
            emailAddress:['', RxwebValidators.containsAsync({value:'@gmail.com' })], 
            otherEmailAddress:['', RxwebValidators.containsAsync({value:'@gmail.com'  ,message:'Please enter valid gmailId' })], 
            organizationEmailAddress:['', RxwebValidators.containsAsync({value:'@gmail.com'  ,messageKey:'containsMessageKey' })], 
        });
    }
}

