import { Component, OnInit } from '@angular/core';
import { FormGroup,Validators } from "@angular/forms"
import { UserInfo } from './user-info.model';

@Component({
    selector: 'app-date-complete-template-driven-validation-decorators',
    templateUrl: './date-complete.component.html'
})
export class DateCompleteTemplateDrivenValidationDecoratorsComponent implements OnInit {
    userinfo: UserInfo
	
    constructor(
    ) { }

    ngOnInit() {
       this.userinfo= new UserInfo()
    }
}

complete.model:
import {  date, } from   "@rxweb/reactive-form-validators"   

export class UserInfo {

	@date() 
	birthDate: string;
	
	
	//If you want to apply conditional expression of type 'function'
	@date({conditionalExpression:(x,y) => x.birthDate == "16/04/1997"  }) 
	admissionDate: string;
	
	
	//If you want to apply conditional expression of type 'string'
	@date({conditionalExpression:'x => x.birthDate =="16/04/1997"' }) 
	enrollmentDate: string;
	
	
	@date({message:'{{0}} is not a valid date' }) 
	allocationDate: string;
	
	
}

