import { Component, OnInit } from '@angular/core';
import { FormGroup,Validators } from "@angular/forms"
import { UserInfo } from './user-info.model';

@Component({
    selector: 'app-dateAsync-complete-template-driven-validation-decorators',
    templateUrl: './date-async-complete.component.html'
})
export class DateAsyncCompleteTemplateDrivenValidationDecoratorsComponent implements OnInit {
    userinfo: UserInfo
	
    constructor(
    ) { }

    ngOnInit() {
       this.userinfo= new UserInfo()
    }
}

complete.model:
import {  dateAsync, } from   "@rxweb/reactive-form-validators"   

export class UserInfo {

	@dateAsync() 
	birthDate: string;
	
	
	@dateAsync({allowISODate:true }) 
	registrationDate: string;
	
	
	//If you want to apply conditional expression of type 'function'
	@dateAsync({conditionalExpression:(x,y) => x.birthDate == "16/04/1997"  }) 
	admissionDate: string;
	
	
	@dateAsync({message:'{{0}} is not a valid date' }) 
	allocationDate: string;
	
	
	@dateAsync({messageKey:'dateMessageKey' }) 
	confirmationDate: string;
	
	
}

