    selector: 'app-endsWithAsync-complete-template-driven-validation-decorators',
    templateUrl: './ends-with-async-complete.component.html'
})
export class EndsWithAsyncCompleteTemplateDrivenValidationDecoratorsComponent implements OnInit {
    user: User
	
    constructor(
    ) { }

    ngOnInit() {
       this.user= new User()
    }
}

complete.model:
import {  endsWithAsync, } from   "@rxweb/reactive-form-validators"   

export class User {

	@endsWithAsync({validatorConfig:of({value:'t'})}) 
	name: string;
	
	
	@endsWithAsync({validatorConfig:of({value:'b'  ,message:'{{0}} does not ends with `b`'})}) 
	company: string;
	
	
	@endsWithAsync({validatorConfig:of({value:'r'  ,messageKey:'endsWithMessageKey'})}) 
	skill: string;
	
	
}

