import { Component, OnInit } from '@angular/core';
import { FormGroup,Validators } from "@angular/forms"
import { UserInfo } from './user-info.model';

@Component({
    selector: 'app-extensionAsync-complete-template-driven-validation-decorators',
    templateUrl: './extension-async-complete.component.html'
})
export class ExtensionAsyncCompleteTemplateDrivenValidationDecoratorsComponent implements OnInit {
    userinfo: UserInfo
					fileTypes = [ "Picture", "Document",];

    constructor(
    ) { }

    ngOnInit() {
       this.userinfo= new UserInfo()
    }
}

complete.model:
import {  extensionAsync,prop, } from   "@rxweb/reactive-form-validators"   

export class UserInfo {

	@prop()
	fileType: string;

	@extensionAsync({extensions:['jpg','bmp'] }) 
	profilePicture: string;
	
	
	@extensionAsync({extensions:['vcf']  ,message:'You can upload only .vcf files.' }) 
	contactFile: string;
	
	
	@extensionAsync({extensions:['xls','xlsx']  ,messageKey:'extensionMessageKey' }) 
	excelFile: string;
	
	
}

