	@factorAsync({validatorConfig:of({dividend:30  ,message:'{{0}} is not a factor of 50'})}) 
	sixthNumber: number;
	
	
	@factorAsync({validatorConfig:of({dividend:50  ,messageKey:'factorMessageKey'})}) 
	seventhNumber: number;
	
	
}

