import { Component, OnInit } from '@angular/core';
import { FormGroup,Validators } from "@angular/forms"
import { User } from './user.model';

@Component({
    selector: 'app-greaterThanAsync-complete-template-driven-validation-decorators',
    templateUrl: './greater-than-async-complete.component.html'
})
export class GreaterThanAsyncCompleteTemplateDrivenValidationDecoratorsComponent implements OnInit {
    user: User
	
    constructor(
    ) { }

    ngOnInit() {
       this.user= new User()
    }
}

complete.model:
import {  greaterThanAsync,prop, } from   "@rxweb/reactive-form-validators"   

export class User {

	@prop()
	age: number;

	@greaterThanAsync({validatorConfig:of({fieldName:'age'})}) 
	retiermentAge: number;
	
	
	@greaterThanAsync({validatorConfig:of({value:18})}) 
	minimumAge: number;
	
	
	@greaterThanAsync({validatorConfig:of({fieldName:'age'  ,message:'Please enter number greater than 0.'})}) 
	otherAge: number;
	
	
	@greaterThanAsync({validatorConfig:of({fieldName:'age'  ,messageKey:'greaterThanMessageKey'})}) 
	joiningAge: number;
	
	
}

