import { Component, OnInit } from '@angular/core';
import { FormGroup,Validators } from "@angular/forms"
import { User } from './user.model';

@Component({
    selector: 'app-greaterThanEqualToAsync-complete-template-driven-validation-decorators',
    templateUrl: './greater-than-equal-to-async-complete.component.html'
})
export class GreaterThanEqualToAsyncCompleteTemplateDrivenValidationDecoratorsComponent implements OnInit {
    user: User
	
    constructor(
    ) { }

    ngOnInit() {
       this.user= new User()
    }
}

complete.model:
import {  greaterThanEqualToAsync,prop, } from   "@rxweb/reactive-form-validators"   

export class User {

	@prop()
	admissionAge: number;

	@greaterThanEqualToAsync({fieldName:'admissionAge' }) 
	retiermentAge: number;
	
	
	@greaterThanEqualToAsync({value:18 }) 
	minimumAge: number;
	
	
	@greaterThanEqualToAsync({fieldName:'admissionAge'  ,message:'Please enter number greater than or equal to 1.' }) 
	otherAge: number;
	
	
	@greaterThanEqualToAsync({fieldName:'admissionAge'  ,messageKey:'greaterThanEqualToMessageKey' }) 
	joiningAge: number;
	
	
}

