import { Component, OnInit } from '@angular/core';
import { FormGroup,Validators } from "@angular/forms"
import { UserInfo } from './user-info.model';

@Component({
    selector: 'app-iban-complete-template-driven-validation-decorators',
    templateUrl: './iban-complete.component.html'
})
export class IbanCompleteTemplateDrivenValidationDecoratorsComponent implements OnInit {
    userinfo: UserInfo
	
    constructor(
    ) { }

    ngOnInit() {
       this.userinfo= new UserInfo()
    }
}

complete.model:
import {  iban, } from   "@rxweb/reactive-form-validators"   

export class UserInfo {

	@iban({countryCode:'US' }) 
	gBBankAccountNumber: string;
	
	
	@iban({countryCode:'BH'  ,conditionalExpression:'x => x.gBBankAccountNumber == "GB33BUKB20201555555555"' }) 
	bHBankAccountNumber: string;
	
	
	@iban({countryCode:'BR'  ,conditionalExpression:'(x,y) => x.bRBankAccountNumber == "GB33BUKB20201555555555"' }) 
	bRBankAccountNumber: string;
	
	
	@iban({countryCode:'CR'  ,message:'You must enter a Costa Rica account number .' }) 
	cRBankAccountNumber: string;
	
	
	@iban({messageKey:ibanMessageKey  ,countryCode:'CH' }) 
	cHBankAccountNumber: string;
	
	
}

