import {  imageAsync,prop, } from   "@rxweb/reactive-form-validators" 
import { of } from "rxjs" 

   

export class UserInfo {

	@prop()
	imageType: string;

	@imageAsync({maxHeight:100  ,maxWidth:100 }) 
	profilePhoto: string;
	
	
	@imageAsync({maxHeight:100  ,maxWidth:100 }) 
	signature: string;
	
	
	@imageAsync({minHeight:10  ,maxHeight:100  ,minWidth:10  ,maxWidth:10 }) 
	voterId: string;
	
	
	@imageAsync({minHeight:10  ,maxHeight:100  ,maxWidth:100 }) 
	identityCard: string;
	
	
	@imageAsync({maxHeight:100  ,maxWidth:100 }) 
	drivinglicense: string;
	
	
	@imageAsync({maxHeight:100  ,maxWidth:100  ,message:'File exceed maximum Height.' }) 
	residenceProof: string;
	
	
	@imageAsync({maxHeight:100  ,maxWidth:100  ,messageKey:'imageMessageKey' }) 
	passport: string;
	
	
}

