    ) { }

    ngOnInit() {
       this.user= new User()
    }
}

complete.model:
import {  lessThanEqualToAsync,prop, } from   "@rxweb/reactive-form-validators"   

export class User {

	@prop()
	totalMarks: number;

	@lessThanEqualToAsync({fieldName:'totalMarks' }) 
	passingMarks: number;
	
	
	@lessThanEqualToAsync({value:60 }) 
	maximumAge: number;
	
	
	@lessThanEqualToAsync({fieldName:'totalMarks'  ,message:'Please enter number less than 100.' }) 
	otherMarks: number;
	
	
	@lessThanEqualToAsync({fieldName:'totalMarks'  ,messageKey:'lessThanEqualToMessageKey' }) 
	disciplineMarks: number;
	
	
}

