complete.component:
import { Component, OnInit } from '@angular/core';
import { FormGroup,Validators } from "@angular/forms"
import { User } from './user.model';

@Component({
    selector: 'app-mask-complete-template-driven-validation-directives',
    templateUrl: './mask-complete.component.html'
})
export class MaskCompleteTemplateDrivenValidationDirectivesComponent implements OnInit {
    user: User
	
    constructor(
    ) { }

    ngOnInit() {
       this.user= new User()
    }
}

complete.model:
export class User {

	firstName: string;

	phoneNumber: string;
	
	
	postalCode: string;
	
	
	zipcode: string;
	
	
	contactNumber: string;
	
	
	alternateContactNumber: string;
	
	
}

