import { Component, OnInit } from '@angular/core';
import { FormGroup,Validators } from "@angular/forms"
import { User } from './user.model';

@Component({
    selector: 'app-maxDateAsync-complete-template-driven-validation-decorators',
    templateUrl: './max-date-async-complete.component.html'
})
export class MaxDateAsyncCompleteTemplateDrivenValidationDecoratorsComponent implements OnInit {
    user: User
	
    constructor(
    ) { }

    ngOnInit() {
       this.user= new User()
    }
}

complete.model:
import {  maxDateAsync,prop, } from   "@rxweb/reactive-form-validators"   

export class User {

	@prop()
	userName: string;

	@maxDateAsync({value:'07/30/2018' }) 
	allocationDate: string;
	
	
	@maxDateAsync({value:'07/30/2018'  ,message:'{{0}} exceeds the Maximum Date Limit' }) 
	registrationDate: string;
	
	
	@prop()
	enrollmentDate: string;

	@maxDateAsync({fieldName:'enrollmentDate' }) 
	lastRegistrationDate: string;
	
	
	@maxDateAsync({value:'07/30/2018'  ,operator:'<' }) 
	confirmationDate: string;
	
	
	@maxDateAsync({value:'07/30/2018'  ,messageKey:'maxDateMessageKey' }) 
	resignationDate: string;
	
	
}

