import { Component, OnInit } from '@angular/core';
import { FormGroup,Validators } from "@angular/forms"
import { SubjectDetails } from './subject-details.model';

@Component({
    selector: 'app-maxNumberAsync-complete-template-driven-validation-directives',
    templateUrl: './max-number-async-complete.component.html'
})
export class MaxNumberAsyncCompleteTemplateDrivenValidationDirectivesComponent implements OnInit {
    subjectdetails: SubjectDetails
	
    constructor(
    ) { }

    ngOnInit() {
       this.subjectdetails= new SubjectDetails()
    }
}

