import { Component, OnInit } from '@angular/core';
import { FormGroup,Validators } from "@angular/forms"
import { User } from './user.model';

@Component({
    selector: 'app-minDateAsync-complete-template-driven-validation-decorators',
    templateUrl: './min-date-async-complete.component.html'
})
export class MinDateAsyncCompleteTemplateDrivenValidationDecoratorsComponent implements OnInit {
    user: User
	
    constructor(
    ) { }

    ngOnInit() {
       this.user= new User()
    }
}

complete.model:
import {  minDateAsync,prop, } from   "@rxweb/reactive-form-validators"   

export class User {

	@prop()
	userName: string;

	@minDateAsync({value:'07/30/2018' }) 
	allocationDate: string;
	
	
	@minDateAsync({value:'07/30/2018'  ,message:'{{0}} exceeds the Minimum Date Limit' }) 
	registrationDate: string;
	
	
	@prop()
	enrollmentDate: string;

	@minDateAsync({fieldName:'enrollmentDate' }) 
	lastRegistrationDate: string;
	
	
	@minDateAsync({value:'07/30/2018'  ,operator:'>' }) 
	confirmationDate: string;
	
	
	@minDateAsync({value:'07/30/2018'  ,messageKey:'minDateMessageKey' }) 
	resignationDate: string;
	
	
}

