export class ResourceDetailsComponent implements OnInit {
    resourceDetailFormGroup : FormGroup;
 
    constructor(private formBuilder:RxFormBuilder){}
 
     ngOnInit(){
         let resourceDetails =  new ResourceDetails();
         resourceDetails.resourceDetails = new  Array<Department>();
         resourceDetails.resourceDetails.push(new Department);
         this.resourceDetailFormGroup = this.formBuilder.formGroup(resourceDetails);
     }
 
     getFormArray(){
         let formArray = this.resourceDetailFormGroup.controls.resourceDetails as FormArray;
         return formArray.controls;
     }
}

