import { DashboardComponent } from './dashboard/dashboard.component';
import { RxWebModule } from './rxweb.module';
import { AuthResolver } from './security/authentication-resolver';

@routerModule({
  authentication:AuthResolver
})
@NgModule({
  declarations: [
    AppComponent,
    LoginComponent,
    DashboardComponent
  ],
  imports: [
    RxRoutingModule,
     BrowserModule,
    AppRoutingModule
  ],
  providers: [],
  bootstrap: [AppComponent]
})
export class AppModule { }

