import { Component, OnInit } from '@angular/core';
import { FormGroup,Validators } from "@angular/forms"
import { AttandanceDetail } from './attandance-detail.model';

@Component({
    selector: 'app-timeAsync-complete-template-driven-validation-decorators',
    templateUrl: './time-async-complete.component.html'
})
export class TimeAsyncCompleteTemplateDrivenValidationDecoratorsComponent implements OnInit {
    attandancedetail: AttandanceDetail
	
    constructor(
    ) { }

    ngOnInit() {
       this.attandancedetail= new AttandanceDetail()
    }
}

complete.model:
import {  timeAsync,prop, } from   "@rxweb/reactive-form-validators" 
import { of } from "rxjs" 

   

export class AttandanceDetail {

	@prop()
	entryPlace: string;

	@timeAsync({validatorConfig:of({allowSeconds:true})}) 
	totalOutTime: string;
	
	
	@timeAsync({validatorConfig:of({message:'You can enter only time format data'})}) 
	exitTime: string;
	
	
	@timeAsync({validatorConfig:of({messageKey:'timeMessageKey'})}) 
	netInTime: string;
	
	
}

