import { DynamicFormBuildConfig, DynamicFormConfiguration, RxFormBuilder } from "@rxweb/reactive-form-validators";

@Component({
    selector: 'app-cssClassNames-static',
    templateUrl: './cssClassNames-static.component.html'
})
export class CssClassNamesStaticComponent implements OnInit {

    serverData: Array<{ [key: string]: any}> = [
        {
            name: 'firstName',
            type: 'textbox',
            ui: {
                cssClassNames: ['col-6','font-italic'],
                label: 'First Name'
            }
        }
    ]

    viewMode: string = 'bootstrap-basic';
    uiBindings: string[] = ['firstName'];
    dynamicFormBuildConfig: DynamicFormBuildConfig;

    constructor(private formBuilder: RxFormBuilder) {}

    ngOnInit() {
        this.dynamicFormBuildConfig = this.formBuilder.dynamicForm(this.serverData);
    }
}

conditional.component:
import { Component, OnInit } from "@angular/core";
import { DynamicFormBuildConfig, DynamicFormConfiguration, RxFormBuilder, action, FormControlConfig } from "@rxweb/reactive-form-validators";
import { CssClassNamesModel } from "./cssClassName.model";

@Component({
    selector: 'app-cssClassNames-conditional',
    templateUrl: './cssClassNames-conditional.component.html'
})
export class CssClassNamesConditionalComponent implements OnInit {

    serverData: Array<{ [key: string]: any}> = [
        {
            name: 'firstName',
            type: 'textbox',
            ui: {
                cssClassNames: ['col-6','font-italic'],
                label: 'First Name'
            }
        },
        {
            name: 'lastName',
            type: 'textbox',
            actionKeyNames: ["onlyCssClassNames"],
            modelName: 'cssClassNames',
            ui: {
                label: 'Last Name'
            }
        }
    ]

    viewMode: string = 'bootstrap-basic';
    uiBindings: string[] = ['firstName','lastName'];
    dynamicFormBuildConfig: DynamicFormBuildConfig;
    dynamicFormConfiguration: DynamicFormConfiguration;

    constructor(private formBuilder: RxFormBuilder) {}

    ngOnInit() {
        this.dynamicFormConfiguration = {
            fieldConfigModels: [{
                modelName: 'cssClassNames',
                model: CssClassNamesModel
            }]
        }
        this.dynamicFormBuildConfig = this.formBuilder.dynamicForm(this.serverData,this.dynamicFormConfiguration);
    }
}

conditional.model:
import { FormControlConfig, action } from "@rxweb/reactive-form-validators";

@action([
    {
        keyName: 'onlyCssClassNames',
        actions: {
            cssClassNames: function () {
                if (this.controlsConfig.firstName.value == 'John') {
                    return this.controlsConfig.lastName.cssClassNames = ['col-6']
                }
                return null
            }
        }
    }
])
export class CssClassNamesModel extends FormControlConfig { }
