import { Component,OnInit } from "@angular/core";
import { DynamicFormBuildConfig, DynamicFormConfiguration, RxFormBuilder } from "@rxweb/reactive-form-validators";

@Component({
    selector: 'app-dropdown-static',
    templateUrl: './dropdown-static.component.html'
})


export class DropDownStaticComponent implements OnInit {
    serverData: Array<{ [key: string]: any }> = [{        
            name: "country",
            type: "select",
            source: [{ value: 1, key: "India" }],
            ui: {
                label: 'Country',
                placeholder: 'Select',
                            
            }
    }]
    viewMode:string = "bootstrap-basic";
    uiBindings:string[] = ["country"];
    dynamicFormBuildConfig: DynamicFormBuildConfig;
    constructor(private formBuilder: RxFormBuilder) { }
    ngOnInit()
    {
        this.dynamicFormBuildConfig = this.formBuilder.dynamicForm(this.serverData);
    }
}

cascading.component:
import { Component,OnInit } from "@angular/core";
import { DynamicFormBuildConfig, DynamicFormConfiguration, RxFormBuilder, FormControlConfig } from "@rxweb/reactive-form-validators";
import { SourceModel } from "./source";



@Component({
    selector: 'app-dropdown-cascading',
    templateUrl: './dropdown-cascading.component.html'
})

export class DropDownCascadingComponent implements OnInit {
    serverData: Array<{ [key: string]: any }> = [{        
        name: "country",
        type: "select",
        source: [{ value: 1, key: "India" },{value:2,key:"Australia"}],
        ui: {
            label: 'Country',
            placeholder: 'Select',
                        
        }
    },
    {
        name: "state",
         type: "select",
         source: [{ value: 1, key: "Gujarat", countryId: 1 }, { value: 2, key: "Delhi", countryId: 1 },{ value: 3, key: "Melbourne", countryId: 2 }],
         actionKeyNames: ["nonAsyncSource"],
         modelName:'source',
         ui: {
            label: 'State',
            placeholder: 'Select'                            
        }
    }]
    viewMode:string = "bootstrap-basic";
    uiBindings:string[] = ["country","state"];
    dynamicFormBuildConfig: DynamicFormBuildConfig;
    dynamicFormConfiguration: DynamicFormConfiguration;
    constructor(private formBuilder: RxFormBuilder) { }
    ngOnInit()
    {
        this.dynamicFormConfiguration = {
            fieldConfigModels:[{
                modelName:'source',
                model:SourceModel
            }]
        }
      this.dynamicFormBuildConfig = this.formBuilder.dynamicForm(this.serverData,this.dynamicFormConfiguration);
    }
}

cascading.model:
import {action,FormControlConfig} from '@rxweb/reactive-form-validators'
@action([
    {
        keyName:'asyncSource',
        actions : {
            filter:function(){
                let promise = new Promise<any[]>((resolve, reject) => {
                    resolve(this.config.source.filter(t => t.countryId == this.dynamicModel.countryId.value));
                });
                return promise
            }
        }
    },
    {
        keyName:'nonAsyncSource',
        actions: {
            filter:function(){
                if (this.controlsConfig && this.controlsConfig.country.value)
                return this.config.source.filter(t => t.countryId == this.controlsConfig.country.value);
            else
                return [];
            }
        }
    }
])
export class SourceModel extends FormControlConfig{
    
}

